long amount = 0;
for (int i = 0; i < w.getNumberOfTransactions(this.principal); i++) {
Transaction t = w.getTransaction(principal, i);
GameTime time = w.getTransactionTimeStamp(principal, i);
if (t instanceof DeliverCargoReceipt
