ImPoint returnValue = next;
int x = next.x;
int y = next.y;
if (index < vectors.size()) {
Step s = vectors.get(index);
x += s.deltaX;
y += s.deltaY;
next = new ImPoint(x, y);
} else {
next = null;
}
index++;
return returnValue;
}
