public PositionOnTrack getFinalPosition() {
int x = start.x;
int y = start.y;
for (int i = 0; i < vectors.size(); i++) {
Step v = vectors.get(i);
x += v.deltaX;
y += v.deltaY;
}
int i = vectors.size() - 1;
Step finalStep = vectors.get(i);
PositionOnTrack p = PositionOnTrack.createFacing(x, y, finalStep);
return p;
}

